import React, { useState } from "react";
import {Switch, Paper,Grid ,Typography} from "@material-ui/core/"
import { View,StyleSheet, Text } from "react-native";
import {ThemeProvider, createTheme,} from "@material-ui/core/styles";

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      type: darkMode ? "dark" : "light",
    },

    typography: {
      fontSize: darkMode ? "medium" : "small",
    }

  });




  //const trocaFont = {};

  return (
    <ThemeProvider theme={theme}>
      <Paper style={{height:'100vh'}}>
        <Grid alignItems="center"></Grid>

        <Switch checked={darkMode} onChange={ () => setDarkMode(!darkMode)}>Teste</Switch>
        <View >
        <Typography variant="h2">Teste gigante</Typography>

        </View>
      </Paper>
    </ThemeProvider>

  );
}



export default App;
