function App() {
  //const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      type: 'dark',
    },

  });




  //const trocaFont = {};

  return (
    <ThemeProvider theme={theme}>
      <Paper style={{height:'100vh'}}>
        <Grid container direction ="column"></Grid>
        <Switch color="#ff0000">Teste</Switch>
      </Paper>
    </ThemeProvider>

  );
}



export default App;
