    <View>
      <View >
        <View>
          <Text>Dia</Text>
          <Text>{enabled ? "Claro" : "Escuro"}</Text>
          <Switch
            trackColor={{ false: "#767577", true: "#46c186" }}

            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={enabled}
            style={{
              margin: 10,
              transform: [{ scaleX: 1.5 }, { scaleY: 1.5 }],
            }}
          />
        </View>
        <View style={styles.bar}>
          <Text>Fonte</Text>
          <Text>{enabled ? "Pequena" : "Grande"}</Text>
          <Switch
            trackColor={{ false: "#767577", true: "#46c186" }}

            ios_backgroundColor="#3e3e3e"
            checked={darkMode} onChange={() => setDarkMode(!darkMode)}
            value={enabled}
            style={{
              margin: 10,
              transform: [{ scaleX: 1.5 }, { scaleY: 1.5 }],
            }}
          />
        </View>
      </View>
    </View>
    </Paper>
    </ThemeProvider>
  );
}

export default App;
